mapper.update:
UPDATE curriculum
SET curriculum_name = #{curriculumName}, teacher_id = #{teacherId}, teacher_name = #{teacherName}, price = #{price}, stock = #{stock}, stock_used = #{stockUsed}, begin_time = #{beginTime}, end_time = #{endTime}, info = #{info}
WHERE curriculum_id = #{curriculumId}

UPDATE curriculum
SET stock_used = stock_used + 1
Where stock - stock_used > 0 AND curriculum_id = #{curriculumId}

mapper.select:
SELECT curriculum_id, curriculum_name, teacher_id, teacher_name, price, stock, stock_used, begin_time, end_time, info
FROM curriculum


        curriculum_name LIKE #{curriculumName}


LIMIT #{page}, #{pageSize}

SELECT curriculum_id, curriculum_name, teacher_id,teacher_name, price, stock, stock_used, begin_time, end_time, info
FROM curriculum
WHERE curriculum_id = #{curriculumId}
